searchUrl:
@js:
e = {"#": "novel", "!": "comic", "%": "mv"};
r = {"novel": "小说", "comic": "漫画", "mv": "视频"};
let type = "novel";
let keyWithoutSymbol = key;
let isFirstCharChinese = key.match(/^[\u4e00-\u9fff]/) ;
let isFirstCharValidPrefix = key[0] in e;
if (isFirstCharChinese!=null) {
    java.longToast("搜索无前缀，默认为小说搜索模式");
  } else if (isFirstCharValidPrefix==true) {
    type = e[key[0]];
    keyWithoutSymbol = key.slice(1);
    let typetext = r[type];
    java.longToast("当前为" + typetext + "搜索模式");
  } else if (isFirstCharValidPrefix==false) {
    java.longToast("搜索前缀无效，默认为小说搜索模式");
    keyWithoutSymbol = key.slice(1);
  }
let url = "https://www.18mh.net/" + type + "/all?key_word=" + keyWithoutSymbol + "&page=" + page;
url;

