@js:
var result = [];
var push = (title, url, size) => result.push({"title": title,"url": url,"style": {"layout_flexGrow": 1,"layout_flexBasisPercent": size}});
var tagUrl = source.key;
var tag = java.ajax(tagUrl);
var tagList = org.jsoup.Jsoup.parse(tag).select(".nav a");
var tagSize = 0.4;
push("分类",null,1);
for (var i=1;i < tagList.length-1;i++) {
    var title = tagList[i].text();
    var url = source.key + String(tagList[i].attr("href")).replace(/(\d+)\/$/, `{{page}}`);
    title = String(title).replace(/\s/g, "");
    push(title, url, tagSize);
}
var spaceLength = tagList.length % 2;
for (i = 0; i < spaceLength; i++) {
     push("——",null,tagSize);
}
JSON.stringify(result);

