let novelId = baseUrl.split('/').pop().match(/\d+/)[0]
function formatChapterLinks(htmlStr) {
  let aTagRegex = /<a\s+href="([^"]+)"[^>]*>([^<]+)<\/a>/g
  let result = []
  let match
  while ((match = aTagRegex.exec(htmlStr)) !== null) {
    let href = match[1]
    let chapName = match[2]
    let chapId = href.replace(/^\/book\//, '').replace(/\.html$/, '').replace('/', 'U')
    result.push({
      href: href,
      chapName: chapName,
      chapId: chapId
    })
  }
  return result
}
let chaps = []

let token = String(source.get('token'))
if (token && !result) {
  let result = java.ajax(getApiUrl('/volumelist', { novelId: novelId }, this))
  chaps = JSON.parse(result).data || []
} else {
  chaps = formatChapterLinks(result)
}
let dirs = []
if (token) {
  try {
    let res = java.ajax(getApiUrl('/dirs', { novelId: novelId }, this))
    dirs = JSON.parse(res).data || []
  } catch { }
}
let dirsData = dirs.reduce((acc, d) => {
  acc[d.chapId] = d
  return acc
}, {})
let idsSet = new Set(Object.keys(dirsData))

chaps = chaps.map(v => {
  let isShare = idsSet.has(v.chapId)
  let tag = isShare ? 'ğŸ' : ''
  return {
    url: getApiUrl('/chaps', { chapId: v.chapId, novelId: novelId }, this),
    chapName: tag + v.chapName,
    vinfo: isShare ? `     ${dirsData[v.chapId].charCount || 0}å­—` : '',
  }
})
