	var html=`<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no">

    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            -webkit-tap-highlight-color: transparent;
        }

        body {
            background: #000;
            color: #fff;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            height: 100vh;
            overflow: hidden;
            position: relative;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .player-container {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: #000;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            z-index: 100;
            overflow: hidden;
        }

        video {
            max-width: 100%;
            max-height: 100%;
            transition: all 0.5s ease;
            background: #000;
        }

        .player-container.landscape video {
            max-width: 70%;
            max-height: 70%;
        }

        .start-overlay {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            background: rgba(0,0,0,0.7);
            z-index: 15;
        }

        .start-button {
            width: 100px;
            height: 100px;
            border-radius: 50%;
            background: rgba(255, 51, 102, 0.8);
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 2.5rem;
            color: white;
            cursor: pointer;
            box-shadow: 0 0 30px rgba(255, 51, 102, 0.6);
            animation: pulse 2s infinite;
            transition: transform 0.2s;
        }

        .start-button:active {
            transform: scale(0.95);
        }

        @keyframes pulse {
            0% { transform: scale(1); box-shadow: 0 0 0 0 rgba(255, 51, 102, 0.7); }
            70% { transform: scale(1.05); box-shadow: 0 0 0 15px rgba(255, 51, 102, 0); }
            100% { transform: scale(1); box-shadow: 0 0 0 0 rgba(255, 51, 102, 0); }
        }

        .start-text {
            margin-top: 25px;
            font-size: 1.3rem;
            text-align: center;
            max-width: 80%;
            line-height: 1.5;
            color: rgba(255, 255, 255, 0.9);
            text-shadow: 0 1px 3px rgba(0,0,0,0.5);
        }

        .controls {
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
            padding: 20px 15px 35px;
            display: flex;
            flex-direction: column;
            z-index: 10;
            opacity: 0;
            transition: opacity 0.3s;
        }

        .player-container.show-controls .controls {
            opacity: 1;
        }

        .progress-container {
            width: 100%;
            height: 6px;
            background: rgba(255,255,255,0.2);
            border-radius: 3px;
            margin-bottom: 20px;
            position: relative;
            cursor: pointer;
            overflow: hidden;
        }

        .progress-bg {
            position: absolute;
            top: 0;
            left: 0;
            height: 100%;
            width: 100%;
            background: rgba(255,255,255,0.1);
            border-radius: 3px;
        }

        .progress {
            position: absolute;
            top: 0;
            left: 0;
            height: 100%;
            width: 0%;
            background: #ff3366;
            border-radius: 3px;
            transition: width 0.1s linear;
        }

        .progress-thumb {
            position: absolute;
            top: 50%;
            right: -8px;
            width: 16px;
            height: 16px;
            background: white;
            border-radius: 50%;
            transform: translateY(-50%);
            box-shadow: 0 0 8px rgba(0,0,0,0.5);
            transition: transform 0.2s, box-shadow 0.2s;
        }

        .progress-container.dragging .progress-thumb {
            transform: translateY(-50%) scale(1.3);
            box-shadow: 0 0 12px rgba(255,255,255,0.7);
        }

        .buffered {
            position: absolute;
            top: 0;
            left: 0;
            height: 100%;
            width: 0%;
            background: rgba(255,255,255,0.3);
            border-radius: 3px;
            transition: width 0.2s;
        }

        .time {
            display: flex;
            justify-content: space-between;
            font-size: 0.85rem;
            color: rgba(255,255,255,0.8);
            margin-bottom: 15px;
        }

        .control-buttons {
            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .play-btn {
            background: rgba(255,255,255,0.15);
            width: 50px;
            height: 50px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-size: 1.8rem;
            transition: transform 0.2s, background 0.2s;
        }

        .play-btn:active {
            transform: scale(0.9);
            background: rgba(255,255,255,0.25);
        }

        .side-buttons {
            display: flex;
            gap: 20px;
        }

        .side-btn {
            color: white;
            font-size: 1.4rem;
            display: flex;
            flex-direction: column;
            align-items: center;
            position: relative;
        }

        .volume-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .volume-slider {
            width: 80px;
            height: 6px;
            background: rgba(255,255,255,0.2);
            border-radius: 3px;
            -webkit-appearance: none;
            appearance: none;
            outline: none;
            transition: all 0.2s;
        }

        .volume-slider::-webkit-slider-thumb {
            -webkit-appearance: none;
            appearance: none;
            width: 14px;
            height: 14px;
            border-radius: 50%;
            background: white;
            cursor: pointer;
            transition: transform 0.2s;
        }

        .volume-slider::-webkit-slider-thumb:active {
            transform: scale(1.3);
        }

        .play-overlay {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            display: flex;
            align-items: center;
            justify-content: center;
            z-index: 8;
            opacity: 0;
            pointer-events: none;
            transition: opacity 0.3s;
        }

        .big-play-btn {
            width: 80px;
            height: 80px;
            background: rgba(255,255,255,0.2);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: white;
            font-size: 2.5rem;
            backdrop-filter: blur(10px);
        }

        .loading {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            font-size: 1.2rem;
            color: rgba(255,255,255,0.7);
            z-index: 5;
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 15px;
        }

        .loading-spinner {
            width: 50px;
            height: 50px;
            border: 4px solid rgba(255,255,255,0.1);
            border-top: 4px solid #ff3366;
            border-radius: 50%;
            animation: spin 1s linear infinite;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .speed-display {
            position: absolute;
            top: 20px;
            right: 20px;
            background: rgba(0,0,0,0.7);
            padding: 8px 15px;
            border-radius: 20px;
            font-size: 0.9rem;
            z-index: 10;
            opacity: 0;
            transition: opacity 0.3s;
            backdrop-filter: blur(5px);
        }

        .player-container.show-speed .speed-display {
            opacity: 1;
        }

        .volume-display {
            position: absolute;
            top: 20px;
            left: 20px;
            background: rgba(0,0,0,0.7);
            padding: 8px 15px;
            border-radius: 20px;
            font-size: 0.9rem;
            z-index: 10;
            opacity: 0;
            transition: opacity 0.3s;
            backdrop-filter: blur(5px);
        }

        .player-container.show-volume .volume-display {
            opacity: 1;
        }

        .preview-thumbnail {
            position: absolute;
            width: 120px;
            height: 70px;
            border-radius: 5px;
            border: 2px solid white;
            box-shadow: 0 5px 15px rgba(0,0,0,0.5);
            transform: translate(-50%, -100%);
            opacity: 0;
            transition: opacity 0.2s;
            pointer-events: none;
            z-index: 20;
            background: #000;
            overflow: hidden;
        }

        .preview-thumbnail img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        .preview-time {
            position: absolute;
            bottom: 5px;
            left: 0;
            right: 0;
            text-align: center;
            font-size: 0.7rem;
            background: rgba(0,0,0,0.7);
            padding: 3px;
        }

        .landscape-btn {
            position: absolute;
            top: 20px;
            left: 50%;
            transform: translateX(-50%);
            background: rgba(0,0,0,0.7);
            color: white;
            padding: 10px 20px;
            border-radius: 30px;
            display: flex;
            align-items: center;
            gap: 10px;
            cursor: pointer;
            z-index: 25;
            transition: all 0.3s;
            backdrop-filter: blur(5px);
            box-shadow: 0 0 15px rgba(0,0,0,0.5);
            opacity: 0;
            pointer-events: none;
            transition: opacity 0.4s ease, transform 0.3s ease;
        }

        .landscape-btn.show {
            opacity: 1;
            pointer-events: all;
        }

        .landscape-btn.hidden {
            opacity: 0;
            pointer-events: none;
        }

        .landscape-btn i {
            font-size: 1.2rem;
        }

        .player-container.landscape .landscape-btn {
            display: flex;
        }

        @media (max-width: 480px) {
            .controls {
                padding: 15px 15px 30px;
            }

            .play-btn {
                width: 45px;
                height: 45px;
                font-size: 1.6rem;
            }

            .side-buttons {
                gap: 15px;
            }

            .volume-slider {
                width: 60px;
            }

            .start-button {
                width: 80px;
                height: 80px;
                font-size: 2rem;
            }

            .start-text {
                font-size: 1.1rem;
            }

            .speed-display, .volume-display {
                padding: 6px 12px;
                font-size: 0.8rem;
            }

            .landscape-btn {
                font-size: 0.9rem;
                padding: 8px 15px;
            }

            .player-container.landscape video {
                max-width: 85%;
                max-height: 85%;
            }
        }

        /* 横屏模式下的特殊样式 */
        .player-container.fullscreen-landscape {
            background: #000;
        }

        .player-container.fullscreen-landscape video {
            max-width: 100%;
            max-height: 100%;
            width: 100%;
            height: 100%;
            object-fit: contain;
        }

        .player-container.fullscreen-landscape .landscape-btn {
            display: none;
        }
    </style>
</head>
<body>
    <div class="player-container" id="playerContainer">
        <video id="videoPlayer">
            <source src="${content}" type="video/mp4">
            您的浏览器不支持HTML5视频播放。
        </video>

        <div class="landscape-btn" id="landscapeBtn">
            <i class="fas fa-expand"></i> 横屏播放
        </div>

        <div class="start-overlay" id="startOverlay">
            <div class="start-button" id="startButton">
                <i class="fas fa-play"></i>
            </div>
            <div class="start-text">点击播放按钮开始观看视频</div>
        </div>

        <div class="controls">
            <div class="progress-container" id="progressContainer">
                <div class="progress-bg"></div>
                <div class="buffered" id="buffered"></div>
                <div class="progress" id="progress">
                    <div class="progress-thumb"></div>
                </div>
                <div class="preview-thumbnail" id="previewThumbnail">
                    <div class="preview-time">00:00</div>
                </div>
            </div>

            <div class="time">
                <span id="currentTime">00:00</span>
                <span id="duration">00:00</span>
            </div>

            <div class="control-buttons">
                <div class="play-btn" id="playBtn">
                    <i class="fas fa-pause"></i>
                </div>

                <div class="side-buttons">
                    <div class="side-btn" id="speedBtn">
                        <i class="fas fa-running"></i>
                    </div>

                    <div class="volume-container">
                        <i class="fas fa-volume-up" id="volumeIcon"></i>
                        <input type="range" class="volume-slider" id="volumeSlider" min="0" max="1" step="0.01" value="0.5">
                    </div>

                    <div class="side-btn" id="fullscreenBtn">
                        <i class="fas fa-expand"></i>
                    </div>
                </div>
            </div>
        </div>

        <div class="play-overlay" id="playOverlay">
            <div class="big-play-btn">
                <i class="fas fa-pause"></i>
            </div>
        </div>

        <div class="loading" id="loading">
            <div class="loading-spinner"></div>
            <div>加载中...</div>
        </div>

        <div class="speed-display" id="speedDisplay">正常速度</div>
        <div class="volume-display" id="volumeDisplay">音量: 50%</div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {

            var video = document.getElementById('videoPlayer');
            var playerContainer = document.getElementById('playerContainer');
            var startOverlay = document.getElementById('startOverlay');
            var startButton = document.getElementById('startButton');
            var playBtn = document.getElementById('playBtn');
            var progress = document.getElementById('progress');
            var progressContainer = document.getElementById('progressContainer');
            var buffered = document.getElementById('buffered');
            var currentTime = document.getElementById('currentTime');
            var duration = document.getElementById('duration');
            var fullscreenBtn = document.getElementById('fullscreenBtn');
            var playOverlay = document.getElementById('playOverlay');
            var speedBtn = document.getElementById('speedBtn');
            var volumeSlider = document.getElementById('volumeSlider');
            var volumeIcon = document.getElementById('volumeIcon');
            var loading = document.getElementById('loading');
            var speedDisplay = document.getElementById('speedDisplay');
            var volumeDisplay = document.getElementById('volumeDisplay');
            var previewThumbnail = document.getElementById('previewThumbnail');
            var previewTime = previewThumbnail.querySelector('.preview-time');
            var landscapeBtn = document.getElementById('landscapeBtn');


            var isDragging = false;
            var speedTimeout, volumeTimeout;
            var isLandscapeMode = false;
            var isFullscreen = false;
            var isAutoLandscape = false;


            playerContainer.classList.add('show-controls');
            video.volume = 0.5;


            function checkFullscreen() {
                isFullscreen = document.fullscreenElement || 
                              document.webkitFullscreenElement || 
                              document.mozFullScreenElement || 
                              document.msFullscreenElement;
                updateLandscapeBtnVisibility();
            }


            function updateLandscapeBtnVisibility() {
                if (isFullscreen) {
                    landscapeBtn.classList.add('hidden');
                } else if (video.videoWidth > video.videoHeight) {
                    landscapeBtn.classList.remove('hidden');
                    landscapeBtn.classList.add('show');
                } else {
                    landscapeBtn.classList.add('hidden');
                    landscapeBtn.classList.remove('show');
                }
            }


            function checkVideoOrientation() {
                if (video.videoWidth > video.videoHeight) {

                    playerContainer.classList.add('landscape');
                    if (!isFullscreen) {
                        landscapeBtn.classList.remove('hidden');
                        landscapeBtn.classList.add('show');
                    }
                } else {
                    playerContainer.classList.remove('landscape');
                    landscapeBtn.classList.add('hidden');
                    landscapeBtn.classList.remove('show');
                }
            }


            function startPlayback() {
                startOverlay.style.display = 'none';
                video.play();
                playBtn.innerHTML = '<i class="fas fa-pause"></i>';
                playerContainer.classList.add('show-controls');


                checkLandscapeMode();
            }

            startButton.addEventListener('click', startPlayback);


            function togglePlay() {
                if (video.paused) {
                    video.play();
                    playBtn.innerHTML = '<i class="fas fa-pause"></i>';
                    playOverlay.innerHTML = '<div class="big-play-btn"><i class="fas fa-pause"></i></div>';
                } else {
                    video.pause();
                    playBtn.innerHTML = '<i class="fas fa-play"></i>';
                    playOverlay.innerHTML = '<div class="big-play-btn"><i class="fas fa-play"></i></div>';
                }

                playOverlay.style.opacity = 1;
                setTimeout(function() {
                    playOverlay.style.opacity = 0;
                }, 500);
            }

            playBtn.addEventListener('click', togglePlay);


            function updateProgress() {
                if (isDragging) return;

                var percent = (video.currentTime / video.duration) * 100;
                progress.style.width = percent + '%';


                var currentMins = Math.floor(video.currentTime / 60);
                var currentSecs = Math.floor(video.currentTime % 60);
                var durationMins = Math.floor(video.duration / 60);
                var durationSecs = Math.floor(video.duration % 60);

                currentTime.textContent = 
                    (currentMins < 10 ? '0' : '') + currentMins + ':' + (currentSecs < 10 ? '0' : '') + currentSecs;

                duration.textContent = 
                    (durationMins < 10 ? '0' : '') + durationMins + ':' + (durationSecs < 10 ? '0' : '') + durationSecs;


                if (video.buffered.length > 0) {
                    var bufferedEnd = video.buffered.end(video.buffered.length - 1);
                    var bufferedPercent = (bufferedEnd / video.duration) * 100;
                    buffered.style.width = bufferedPercent + '%';
                }
            }

            video.addEventListener('timeupdate', updateProgress);


            function startDrag(e) {
                e.preventDefault();
                isDragging = true;
                progressContainer.classList.add('dragging');


                previewThumbnail.style.opacity = 0;


                document.addEventListener('mousemove', drag);
                document.addEventListener('mouseup', endDrag);
                document.addEventListener('touchmove', drag);
                document.addEventListener('touchend', endDrag);


                updateDrag(e);
            }

            function drag(e) {
                if (!isDragging) return;
                updateDrag(e);
            }

            function updateDrag(e) {
                var rect = progressContainer.getBoundingClientRect();
                var position;

                if (e.type.indexOf('touch') !== -1) {
                    position = (e.touches[0].clientX - rect.left) / rect.width;
                } else {
                    position = (e.clientX - rect.left) / rect.width;
                }

                position = Math.max(0, Math.min(1, position));
                var newTime = position * video.duration;


                progress.style.width = (position * 100) + '%';


                var previewMins = Math.floor(newTime / 60);
                var previewSecs = Math.floor(newTime % 60);
                previewTime.textContent = 
                    (previewMins < 10 ? '0' : '') + previewMins + ':' + (previewSecs < 10 ? '0' : '') + previewSecs;


                currentTime.textContent = previewTime.textContent;
            }

            function endDrag(e) {
                if (!isDragging) return;

                var rect = progressContainer.getBoundingClientRect();
                var position;

                if (e.type.indexOf('touch') !== -1) {
                    position = (e.changedTouches[0].clientX - rect.left) / rect.width;
                } else {
                    position = (e.clientX - rect.left) / rect.width;
                }

                position = Math.max(0, Math.min(1, position));
                video.currentTime = position * video.duration;


                document.removeEventListener('mousemove', drag);
                document.removeEventListener('mouseup', endDrag);
                document.removeEventListener('touchmove', drag);
                document.removeEventListener('touchend', endDrag);


                progressContainer.classList.remove('dragging');
                isDragging = false;
            }

            progressContainer.addEventListener('mousedown', startDrag);
            progressContainer.addEventListener('touchstart', startDrag);


            video.addEventListener('waiting', function() {
                loading.style.display = 'flex';
            });

            video.addEventListener('playing', function() {
                loading.style.display = 'none';
            });


            function toggleFullscreen() {
                if (!document.fullscreenElement) {

                    if (playerContainer.requestFullscreen) {
                        playerContainer.requestFullscreen();
                    } else if (playerContainer.webkitRequestFullscreen) {
                        playerContainer.webkitRequestFullscreen();
                    } else if (playerContainer.msRequestFullscreen) {
                        playerContainer.msRequestFullscreen();
                    }
                    isFullscreen = true;
                    playerContainer.classList.add('fullscreen-landscape');
                } else {

                    if (document.exitFullscreen) {
                        document.exitFullscreen();
                    } else if (document.webkitExitFullscreen) {
                        document.webkitExitFullscreen();
                    } else if (document.msExitFullscreen) {
                        document.msExitFullscreen();
                    }
                    isFullscreen = false;
                    playerContainer.classList.remove('fullscreen-landscape');
                }
                updateLandscapeBtnVisibility();
            }

            fullscreenBtn.addEventListener('click', toggleFullscreen);
            landscapeBtn.addEventListener('click', toggleFullscreen);


            function handleFullscreenChange() {
                if (document.fullscreenElement || 
                    document.webkitFullscreenElement || 
                    document.mozFullScreenElement || 
                    document.msFullscreenElement) {
                    isFullscreen = true;
                    playerContainer.classList.add('fullscreen-landscape');
                } else {
                    isFullscreen = false;
                    playerContainer.classList.remove('fullscreen-landscape');


                    if (isLandscapeMode) {
                        enterLandscapeMode();
                    }
                }
                updateLandscapeBtnVisibility();
            }

            document.addEventListener('fullscreenchange', handleFullscreenChange);
            document.addEventListener('webkitfullscreenchange', handleFullscreenChange);
            document.addEventListener('mozfullscreenchange', handleFullscreenChange);
            document.addEventListener('MSFullscreenChange', handleFullscreenChange);


            var controlsTimeout;

            function showControls() {
                playerContainer.classList.add('show-controls');
                clearTimeout(controlsTimeout);
                controlsTimeout = setTimeout(function() {
                    playerContainer.classList.remove('show-controls');
                }, 3000);
            }

            playerContainer.addEventListener('mousemove', showControls);
            playerContainer.addEventListener('touchstart', showControls);


            volumeSlider.addEventListener('input', function() {
                video.volume = this.value;
                updateVolumeIcon();


                volumeDisplay.textContent = '音量: ' + Math.round(video.volume * 100) + '%';
                playerContainer.classList.add('show-volume');
                clearTimeout(volumeTimeout);
                volumeTimeout = setTimeout(function() {
                    playerContainer.classList.remove('show-volume');
                }, 2000);
            });

            function updateVolumeIcon() {
                if (video.volume === 0) {
                    volumeIcon.className = 'fas fa-volume-mute';
                } else if (video.volume < 0.5) {
                    volumeIcon.className = 'fas fa-volume-down';
                } else {
                    volumeIcon.className = 'fas fa-volume-up';
                }
            }


            speedBtn.addEventListener('click', function() {
                var speeds = [0.5, 0.75, 1, 1.25, 1.5, 2];
                var currentSpeed = video.playbackRate;
                var currentIndex = speeds.indexOf(currentSpeed);
                var nextIndex = (currentIndex + 1) % speeds.length;

                video.playbackRate = speeds[nextIndex];


                if (speeds[nextIndex] === 1) {
                    speedDisplay.textContent = '正常速度';
                } else {
                    speedDisplay.textContent = speeds[nextIndex] + 'x 倍速';
                }


                playerContainer.classList.add('show-speed');
                clearTimeout(speedTimeout);
                speedTimeout = setTimeout(function() {
                    playerContainer.classList.remove('show-speed');
                }, 2000);
            });


            video.addEventListener('ended', function() {
                playBtn.innerHTML = '<i class="fas fa-play"></i>';
                playOverlay.innerHTML = '<div class="big-play-btn"><i class="fas fa-play"></i></div>';
                playOverlay.style.opacity = 1;

                if (isFullscreen) {
                    toggleFullscreen();
                } else {
                    startOverlay.style.display = 'flex';
                }
            });


            video.addEventListener('loadedmetadata', function() {
                var mins = Math.floor(video.duration / 60);
                var secs = Math.floor(video.duration % 60);
                duration.textContent = 
                    (mins < 10 ? '0' : '') + mins + ':' + (secs < 10 ? '0' : '') + secs;


                checkVideoOrientation();
                checkFullscreen(); 
            });

            video.addEventListener('resize', checkVideoOrientation);


            updateVolumeIcon();


            landscapeBtn.addEventListener('mouseenter', function() {
